import * as React from "react"

const Alert = React.forwardRef(
  (
    { 
      className, 
      variant = "default", 
      duration = 3000, // Duration in milliseconds (set to null to disable auto-close)
      onClose,
      ...props 
    }, 
    ref
  ) => {
    // Auto-close the popup after the specified duration (if duration and onClose are provided)
    React.useEffect(() => {
      if (duration && onClose) {
        const timer = setTimeout(() => {
          onClose()
        }, duration)
        return () => clearTimeout(timer)
      }
    }, [duration, onClose])

    return (
      <div className="fixed inset-0 flex items-center justify-center z-50">
        {/* Backdrop */}
        <div className="absolute inset-0 bg-black opacity-70"></div>
        {/* Alert box */}
        <div
          ref={ref}
          role="alert"
          className={`relative rounded-lg border p-8 shadow-2xl max-w-sm w-full text-center bg-white
            ${
              variant === "destructive"
                ? "border-red-500 text-red-900 font-semibold"
                : "border-gray-200 text-gray-900"
            } ${className}`}
          {...props}
        />
      </div>
    )
  }
)
Alert.displayName = "Alert"

const AlertDescription = React.forwardRef(({ className, ...props }, ref) => (
  <div ref={ref} className={`text-xl text-center ${className}`} {...props} />
))
AlertDescription.displayName = "AlertDescription"

export { Alert, AlertDescription }
