import React, { useEffect } from "react";
import ReactDOM from "react-dom";

const Modal = ({ isOpen, onClose, title, message, children }) => {
  // Prevent background scrolling when modal is open
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "auto";
    }

    // Cleanup function to reset overflow when component unmounts
    return () => {
      document.body.style.overflow = "auto";
    };
  }, [isOpen]);

  if (!isOpen) return null;

  return ReactDOM.createPortal(
    <div className="fixed inset-0 flex items-center justify-center z-[9999]">
      {/* Backdrop */}
      <div
        className="fixed inset-0 bg-black bg-opacity-75"
        onClick={onClose}
      ></div>

      {/* Modal content */}
      <div className="relative bg-white rounded-lg shadow-xl p-8 max-w-[90%] max-h-[90vh] w-full mx-4 overflow-y-auto">
        {/* Title */}
        {title && (
          <h2 className="text-2xl font-bold text-center text-red-600">
            {title}
          </h2>
        )}

        {/* Message */}
        {message && (
          <p className="mt-4 text-lg text-center">
            {message}
          </p>
        )}

        {/* Custom children content */}
        {children}

        {/* Close button */}
        <div className="mt-6 flex justify-center">
          <button
            onClick={onClose}
            className="px-6 py-2 bg-red-600 text-white rounded hover:bg-red-700 focus:outline-none"
          >
            OK
          </button>
        </div>
      </div>
    </div>,
    document.body // Attach to the document body
  );
};

export default Modal;
