import React, { useState, useEffect, useRef } from "react";
import { ReactP5Wrapper } from "react-p5-wrapper";
import Select from "react-select";
import ReactSlider from "react-slider";
import sketch from "./sketch.js";
import playButton from './icons8-spielen-100.png';
import pauseButton from './icons8-stop-100.png';
import './App.css';

const INITIAL_SPEED = 5;
const MAX_SPEED = 50;

// Define algorithm options exactly as they were in the original code
const algoOptions = [
  { value: "0", label: "Select Algorithm" },
  { value: "1", label: "Nearest Insertion" },
  { value: "2", label: "Farthest Insertion" },
  { value: "3", label: "Nearest Neighbor" },
  { value: "4", label: "Nearest Neighbor Look Ahead (made up)" },
  { value: "5", label: "Brute Force" },
  { value: "7", label: "Christofides" }
];

const localSearchOptions = [
  { value: "0", label: "Select Algorithm" },
  { value: "1", label: "2-opt" },
  { value: "2", label: "3-opt" }
];

export function App() {
  // State management
  const [state, setState] = useState({
    addingNodes: false,
    isRunning: false,
    clearingBoard: false,
    removeEdges: false,
    isPlaying: true,
    speed: INITIAL_SPEED,
    algo: "Not Selected",
    localSearch: "Not Selected",
    runAlgoText: "Run Algorithm",
    localSearchText: "Run Algorithm",
    pathToPic: playButton
  });

  // Helper function for delayed operations
  const delay = (time) => new Promise(resolve => setTimeout(resolve, time / state.speed));

  // Event handlers
  const handleAlgoSelect = (e) => {
    setState(prev => ({
      ...prev,
      addingNodes: false,
      isRunning: false,
      algo: e.label
    }));
  };

  const handleLocalSearchSelect = (e) => {
    setState(prev => ({
      ...prev,
      addingNodes: false,
      isRunning: false,
      localSearch: e.label
    }));
  };

  const handleSpeedSelect = (value) => {
    setState(prev => ({
      ...prev,
      isRunning: false,
      speed: value
    }));
  };

  const handleSetIsPlaying = (isPlaying) => {
    setState(prev => ({
      ...prev,
      isPlaying,
      pathToPic: isPlaying ? pauseButton : playButton
    }));
  };

  const handleRunAlgoButton = async () => {
    setState(prev => {
      const isStartingAlgo = prev.runAlgoText === "Run Algorithm";
      return {
        ...prev,
        addingNodes: false,
        clearingBoard: false,
        removeEdges: !isStartingAlgo,
        isRunning: isStartingAlgo,
        runAlgoText: isStartingAlgo ? "Remove Edges" : "Run Algorithm",
        localSearchText: "Run Algorithm",
        pathToPic: isStartingAlgo ? pauseButton : playButton
      };
    });

    await delay(500);
    setState(prev => ({ ...prev, isRunning: false }));
  };

  const handleLocalSearchButton = async () => {
    setState(prev => ({
      ...prev,
      addingNodes: false,
      clearingBoard: false,
      removeEdges: false,
      isRunning: true,
      pathToPic: pauseButton,
      localSearchText: prev.localSearchText === "Run Algorithm" ? "Run local search" : prev.localSearchText,
      runAlgoText: prev.runAlgoText === "Run Algorithm" ? "Remove Edges" : prev.runAlgoText
    }));

    await delay(500);
    setState(prev => ({ ...prev, isRunning: false }));
  };

  const handlePlayPauseButton = () => {
    setState(prev => ({
      ...prev,
      isPlaying: !prev.isPlaying,
      pathToPic: prev.isPlaying ? playButton : pauseButton
    }));
  };

  const handleAddNodesButton = () => {
    setState(prev => ({
      ...prev,
      addingNodes: !prev.addingNodes,
      isRunning: false,
      clearingBoard: false
    }));
  };

  // Initialize board
  const addNodesButtonRef = useRef();
  useEffect(() => {
    const button = addNodesButtonRef.current;
    button.click();
    const timer = setTimeout(() => button.click(), 3);
    return () => clearTimeout(timer);
  }, []);

  return (
    <>
      <header className="banner">
        <div className="banner__container">
          <h1>Route Optimization Visualizer</h1>
        </div>
      </header>

      <div className="dropdownContainer">
        <Select
          className="dropdown"
          options={algoOptions}
          onChange={handleAlgoSelect}
          defaultValue={{ label: "Select Algorithm", value: 0 }}
        />
      </div>

      <div className="dropdownContainer">
        <Select
          className="dropdown"
          options={localSearchOptions}
          onChange={handleLocalSearchSelect}
          defaultValue={{ label: "Select Algorithm", value: 0 }}
        />
      </div>

      <div className="sliderContainer">
        <label id="slider-label">Choose speed by sliding</label>
        <ReactSlider
          className="slider"
          ariaLabelledby="slider-label"
          thumbClassName="example-thumb"
          trackClassName="example-track"
          max={MAX_SPEED}
          renderThumb={(props, state) => <div {...props}>{state.valueNow}</div>}
          defaultValue={INITIAL_SPEED}
          onChange={handleSpeedSelect}
        />
      </div>

      <main className="Main">
        <div className="Canvas">
          <ReactP5Wrapper
            sketch={sketch}
            addingNodes={state.addingNodes}
            isRunning={state.isRunning}
            clearinBoard={state.clearingBoard}
            algo={state.algo}
            speed={state.speed}
            removeEdges={state.removeEdges}
            localSearch={state.localSearch}
            isPlaying={state.isPlaying}
            setIsPlaying={handleSetIsPlaying}
          />
        </div>
      </main>

      <div className="Buttons">
        <button
          ref={addNodesButtonRef}
          className="RunButton"
          onClick={handleAddNodesButton}
        >
          {state.addingNodes ? 'Stop adding Nodes' : 'Add Nodes'}
        </button>
        <button
          className="RunButton"
          onClick={handleRunAlgoButton}
        >
          {state.runAlgoText}
        </button>
        <button
          className="RunButton"
          onClick={handleLocalSearchButton}
        >
          {state.localSearchText}
        </button>
        <button
          className="RunButton"
          onClick={() => setState(prev => ({
            ...prev,
            addingNodes: false,
            isRunning: false,
            clearingBoard: !prev.clearingBoard
          }))}
        >
          Clear Board
        </button>
        <input
          type="image"
          src={state.pathToPic}
          className="PlayButton"
          onClick={handlePlayPauseButton}
          alt="Play/Pause"
        />
      </div>
    </>
  );
}

export default App;
