import React, { useState, useEffect, useRef } from "react";
import './App.css';
import { ReactP5Wrapper } from "react-p5-wrapper";
import sketch from "./sketch.js";
import Select from "react-select";
import ReactSlider from "react-slider";
import 'rc-slider/assets/index.css';
import playIcon from './icons8-spielen-100.png';
import pauseIcon from './icons8-stop-100.png';

// Configuration constants
const ALGO_OPTIONS = [
  { value: "1", label: "Nearest Insertion" },
  { value: "2", label: "Farthest Insertion" },
  { value: "3", label: "Nearest Neighbor" },
  { value: "4", label: "Nearest Neighbor Look Ahead" },
  { value: "5", label: "Brute Force" },
  { value: "7", label: "Christofides" }
];

const LOCAL_SEARCH_OPTIONS = [
  { value: "1", label: "2-opt" },
  { value: "2", label: "3-opt" }
];

const INITIAL_SPEED = 5;
const SPEED_RANGE = { min: 1, max: 50 };

export default function App() {
  // State management
  const [isAddingNodes, setIsAddingNodes] = useState(false);
  const [isAlgorithmRunning, setIsAlgorithmRunning] = useState(false);
  const [isClearingBoard, setIsClearingBoard] = useState(false);
  const [selectedAlgorithm, setSelectedAlgorithm] = useState("Not Selected");
  const [selectedLocalSearch, setSelectedLocalSearch] = useState("Not Selected");
  const [speed, setSpeed] = useState(INITIAL_SPEED);
  const [isPlaying, setIsPlaying] = useState(false);
  const [shouldRemoveEdges, setShouldRemoveEdges] = useState(false);

  // Refs
  const addNodesButtonRef = useRef();

  // Derived state
  const playPauseIcon = isPlaying ? pauseIcon : playIcon;
  const algorithmButtonText = shouldRemoveEdges ? "Remove Edges" : "Run Algorithm";
  const localSearchButtonText = "Run Local Search";
  const nodesButtonText = isAddingNodes ? 'Stop Adding Nodes' : 'Add Nodes';

  // Effects
  useEffect(() => {
    // Temporary fix for initial state setup
    addNodesButtonRef.current.click();
    setTimeout(() => addNodesButtonRef.current.click(), 3);
  }, []);

  // Handlers
  const handleAlgorithmSelect = ({ label }) => {
    resetVisualizationState();
    setSelectedAlgorithm(label);
  };

  const handleLocalSearchSelect = ({ label }) => {
    resetVisualizationState();
    setSelectedLocalSearch(label);
  };

  const handleSpeedChange = (newSpeed) => {
    resetVisualizationState();
    setSpeed(newSpeed);
  };

  const handleAlgorithmRun = async () => {
    if (algorithmButtonText === "Run Algorithm") {
      startVisualization();
    } else {
      stopVisualization();
    }
    await delay(500);
    setIsAlgorithmRunning(false);
  };

  const handleLocalSearchRun = async () => {
    startVisualization();
    await delay(500);
    setIsAlgorithmRunning(false);
  };

  const handlePlayPauseToggle = () => {
    setIsPlaying(prev => !prev);
  };

  const handleAddNodesToggle = () => {
    resetVisualizationState();
    setIsAddingNodes(prev => !prev);
  };

  const handleClearBoard = () => {
    resetVisualizationState();
    setIsClearingBoard(prev => !prev);
  };

  // Helpers
  const resetVisualizationState = () => {
    setIsAlgorithmRunning(false);
    setIsAddingNodes(false);
    setIsClearingBoard(false);
  };

  const startVisualization = () => {
    setIsAlgorithmRunning(true);
    setShouldRemoveEdges(false);
    setIsPlaying(true);
  };

  const stopVisualization = () => {
    setIsAlgorithmRunning(false);
    setShouldRemoveEdges(true);
    setIsPlaying(false);
  };

  const delay = (ms) => new Promise(resolve => setTimeout(resolve, ms / speed));

  return (
    <div className="app">
      <header className="app-header">
        <h1 className="app-title">Route Optimization Visualizer</h1>
      </header>

      <div className="control-panel">
        <div className="dropdown-group">
          <AlgorithmDropdown 
            options={ALGO_OPTIONS}
            onChange={handleAlgorithmSelect}
            value={selectedAlgorithm}
          />
          <LocalSearchDropdown
            options={LOCAL_SEARCH_OPTIONS}
            onChange={handleLocalSearchSelect}
            value={selectedLocalSearch}
          />
        </div>

        <SpeedSlider 
          speed={speed}
          min={SPEED_RANGE.min}
          max={SPEED_RANGE.max}
          onChange={handleSpeedChange}
        />

        <div className="visualization-container">
          <ReactP5Wrapper
            sketch={sketch}
            addingNodes={isAddingNodes}
            isRunning={isAlgorithmRunning}
            clearBoard={isClearingBoard}
            algorithm={selectedAlgorithm}
            speed={speed}
            removeEdges={shouldRemoveEdges}
            localSearch={selectedLocalSearch}
            isPlaying={isPlaying}
            onPlayPause={setIsPlaying}
          />
        </div>

        <div className="control-buttons">
          <ControlButton onClick={handleAddNodesToggle} ref={addNodesButtonRef}>
            {nodesButtonText}
          </ControlButton>
          <ControlButton onClick={handleAlgorithmRun}>
            {algorithmButtonText}
          </ControlButton>
          <ControlButton onClick={handleLocalSearchRun}>
            {localSearchButtonText}
          </ControlButton>
          <ControlButton onClick={handleClearBoard}>
            Clear Board
          </ControlButton>
          <PlayPauseButton 
            icon={playPauseIcon}
            isPlaying={isPlaying}
            onClick={handlePlayPauseToggle}
          />
        </div>
      </div>
    </div>
  );
}

// Sub-components
const AlgorithmDropdown = ({ options, onChange, value }) => (
  <Select
    className="algorithm-dropdown"
    options={[{ value: "0", label: "Select Algorithm" }, ...options]}
    onChange={onChange}
    value={options.find(opt => opt.label === value)}
    defaultValue={{ label: "Select Algorithm", value: 0 }}
  />
);

const LocalSearchDropdown = ({ options, onChange, value }) => (
  <Select
    className="local-search-dropdown"
    options={[{ value: "0", label: "Select Local Search" }, ...options]}
    onChange={onChange}
    value={options.find(opt => opt.label === value)}
    defaultValue={{ label: "Select Local Search", value: 0 }}
  />
);

const SpeedSlider = ({ speed, min, max, onChange }) => (
  <div className="speed-slider">
    <label>Animation Speed</label>
    <ReactSlider
      className="slider"
      thumbClassName="slider-thumb"
      trackClassName="slider-track"
      min={min}
      max={max}
      value={speed}
      onChange={onChange}
      renderThumb={(props, state) => <div {...props}>{state.valueNow}</div>}
    />
  </div>
);

const ControlButton = React.forwardRef(({ children, ...props }, ref) => (
  <button ref={ref} className="control-button" {...props}>
    {children}
  </button>
));

const PlayPauseButton = ({ icon, onClick }) => (
  <button className="play-pause-button" onClick={onClick}>
    <img src={icon} alt="Play/Pause" className="play-pause-icon" />
  </button>
);
