const AlgorithmAlert = ({ onClose }) => {
    return ReactDOM.createPortal(
      <div className="fixed inset-0 flex items-center justify-center z-[9999]">
        {/* Backdrop */}
        <div 
          className="absolute inset-0 bg-black bg-opacity-50" 
          onClick={onClose}
        ></div>
        {/* Modal content */}
        <div className="relative bg-white rounded-lg shadow-xl p-8 max-w-md w-full mx-4">
          <h2 className="text-2xl font-bold text-center text-red-600">Alert</h2>
          <p className="mt-4 text-lg text-center">
            Please select an algorithm first.
          </p>
          <div className="mt-6 flex justify-center">
            <button
              onClick={onClose}
              className="px-6 py-2 bg-red-600 text-white rounded hover:bg-red-700 focus:outline-none"
            >
              OK
            </button>
          </div>
        </div>
      </div>,
      document.body // This attaches the alert to the document body
    );
  };
